INSERT INTO `page` (`id`, `page_title`, `created_at`, `updated_at`, `page_content`, `status`) VALUES
(10, 'Giới thiệu', '2021-11-23 15:49:16', '2021-11-24 06:40:07', '<p><em>Thương mại điện tử</em>&nbsp;<em>l&agrave; g&igrave;</em>? Đ&acirc;y&nbsp;l&agrave; việc tiến h&agrave;nh một phần hay to&agrave;n bộ hoạt động kinh doanh bằng c&aacute;c phương tiện điện tử. Một c&aacute;ch dễ hiểu hơn th&igrave; &quot;<strong>thương mại điện tử&quot;</strong>&nbsp;ch&iacute;nh l&agrave; việc mua b&aacute;n sản phẩm hay dịch vụ th&ocirc;ng qua internet v&agrave; c&aacute;c phương tiện điện tử kh&aacute;c. C&aacute;c giao dịch n&agrave;y bao gồm tất cả c&aacute;c hoạt động như: giao dịch, mua b&aacute;n, thanh to&aacute;n, đặt h&agrave;ng, quảng c&aacute;o v&agrave; giao h&agrave;ng&hellip;&nbsp;<br />\r\n&ldquo;Bản chất cốt l&otilde;i để web v&agrave; internet ph&aacute;t triển trong tương lai ch&iacute;nh l&agrave; thương mại. C&aacute;c trung t&acirc;m thương mại tr&ecirc;n mạng internet sẽ xuất hiện. N&oacute; sẽ gi&uacute;p c&aacute;c nh&agrave; cung cấp sản phẩm tiếp cận trực tiếp v&agrave; nhanh nhất với người ti&ecirc;u d&ugrave;ng&rdquo;, người nắm giữ chức CEO của Apple tại thời điểm năm 1996 chia sẻ trong hội thảo với chủ đề &ldquo;Steve Jobs: Điều vĩ đại tiếp theo&rdquo;. Thật vậy, hiện nay ng&agrave;nh Thương mại điện tử đang c&oacute; tốc độ ph&aacute;t triển rất mạnh. Hầu hết c&aacute;c c&ocirc;ng ty b&aacute;n h&agrave;ng lập n&ecirc;n ng&agrave;y nay đa số đều l&agrave; c&aacute;c c&ocirc;ng ty thương mại điện tử v&agrave; mua sắm qua mạng đ&atilde; trở th&agrave;nh th&oacute;i quen h&agrave;ng ng&agrave;y của nhiều người Việt Nam.<br />\r\nThế giới ng&agrave;y c&agrave;ng b&ugrave;ng nổ về mạng m&aacute;y t&iacute;nh, con người c&oacute; thể nhanh ch&oacute;ng giao tiếp v&agrave; kết nối với nhau dễ d&agrave;ng hơn th&ocirc;ng qua nhiều loại dich vụ internet. Đ&acirc;y ch&iacute;nh l&agrave; điều kiện thuận lợi cho<em>&nbsp;lĩnh vực Thương mại điện tử</em>&nbsp;ng&agrave;y c&agrave;ng ph&aacute;t triển hơn. Hiện nay, Thương mại điện tử đ&atilde; trở th&agrave;nh một phương tiện giao dịch quen thuộc của c&aacute;c c&ocirc;ng ty thương mại lớn tr&ecirc;n thế giới. Thương mại điện tử c&oacute; khả năng gi&uacute;p &iacute;ch rất nhiều cho những doanh nghiệp cả lớn lẫn nhỏ v&agrave; người hưởng lợi nhất thường l&agrave; kh&aacute;ch h&agrave;ng. Kh&aacute;ch h&agrave;ng sẽ mua được sản phẩm rẻ hơn, nhanh hơn, hiệu quả hơn v&agrave; thuận lợi hơn, c&ograve;n doanh nghiệp c&oacute; thể đưa sản phẩm của m&igrave;nh đến với thị trường một c&aacute;ch nhanh nhất, b&aacute;n h&agrave;ng thuận lợi hơn.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://th.bing.com/th/id/R.41e8b400200ed9875cefaa0304df5cf5?rik=2da%2fkd51GaPbsg&amp;riu=http%3a%2f%2fblog.webtilia.com%2fwp-content%2fuploads%2f2017%2f05%2fecommerce-en-peru-alcance-y-proyeccion-para-el-2018.jpg&amp;ehk=bSipR8xslAu69be8ufjf7XkjjuIVaWKnW73f2ubErrA%3d&amp;risl=&amp;pid=ImgRaw&amp;r=0\" /></p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 1);

