INSERT INTO `post` (`id`, `cat_id`, `post_title`, `images`, `created_at`, `updated_at`, `post_desc`, `post_content`, `featured_posts`, `status`) VALUES
(28, 6, 'Samsung LC32G55: Chiếc màn hình gaming cong 32 inch hỗ trợ HDR, 2K, 144Hz, bao sân trải nghiệm', 'blog1.jpg', '2021-11-24 06:36:48', '2021-11-13 07:07:54', 'Giới thiệu Samsung LC32G55, chiếc màn hình 32 inch cong dành cho game thủ yêu cầu sự trải nghiệm hoành tráng nhưng vẫn cân đối về mặt giá tiền.                                                                                                                        ', '<p style=\"text-align:justify\">Nếu như c&aacute;c ti&ecirc;u chuẩn của m&agrave;n h&igrave;nh gaming cơ bản như độ ph&acirc;n giải FullHD, k&iacute;ch thước 24 v&agrave; 27 inch hay tần số qu&eacute;t 60Hz đ&atilde; kh&ocirc;ng c&ograve;n đủ sức để phục vụ, hay l&agrave;m h&agrave;i l&ograve;ng nhu cầu chơi game của bạn mỗi đ&ecirc;m nữa th&igrave; c&oacute; lẽ đ&atilde; đến l&uacute;c bạn n&ecirc;n đi t&igrave;m cho m&igrave;nh một người y&ecirc;u mới c&oacute; &ldquo;k&iacute;ch thước to&rdquo; hơn, &ldquo;đẹp đẽ&rdquo; hơn, &ldquo;m&agrave;u sắc&rdquo; hơn v&agrave; &ldquo;cử động&rdquo; cũng mượt m&agrave;, uyển chuyển hơn. V&agrave; nếu c&aacute;c bạn vẫn c&ograve;n loay hoay giữa d&ograve;ng xo&aacute;y của duy&ecirc;n phận, chưa lựa cho m&igrave;nh được một bến đỗ hợp l&yacute; th&igrave; h&atilde;y để GVN 360 mai mối cho bạn với một mối t&igrave;nh mang t&ecirc;n Samsung LC32G55, chiếc m&agrave;n h&igrave;nh gaming c&oacute; k&iacute;ch thước lớn &ldquo;32 inch&rdquo;, hỗ trợ độ ph&acirc;n giải &ldquo;2K&rdquo;, &ldquo;HDR&rdquo;, v&agrave; tần số qu&eacute;t &ldquo;144Hz&rdquo;.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-feature-295387287.jpg\" style=\"height:1193px; width:1439px\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Những điểm nổi bật của m&agrave;n h&igrave;nh Samsung LC32G55:</strong></span></h3>\r\n\r\n<p style=\"text-align:justify\">- Thiết kế ngầu, đậm chất gaming</p>\r\n\r\n<p style=\"text-align:justify\">- Độ ph&acirc;n giải 2K, 32 inch, HDR, 144Hz, tốc độ phản hồi 1ms</p>\r\n\r\n<p style=\"text-align:justify\">- Tấm nền VA</p>\r\n\r\n<p style=\"text-align:justify\">- M&agrave;n h&igrave;nh cong 1000R</p>\r\n\r\n<p style=\"text-align:justify\">- T&iacute;ch hợp c&ocirc;ng nghệ AMD FreeSync Premium</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-feature-futuristic-design-295387281.jpg\" style=\"height:1152px; width:1440px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Samsung LC32G55 l&agrave; một chiếc m&agrave;n h&igrave;nh gaming c&oacute; độ ph&acirc;n giải l&ecirc;n tới 2K, k&iacute;ch thước 32 inch, hỗ trợ HDR v&agrave; c&oacute; độ cong 1000R. C&oacute; thể n&oacute;i, đ&acirc;y đều l&agrave; những con số rất k&iacute;ch th&iacute;ch đối với những game thủ n&agrave;o muốn trải nghiệm c&aacute;c tựa game AAA c&oacute; đồ họa đẹp. Đơn giản l&agrave; v&igrave; một chiếc m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước rộng, mức độ chi tiết h&igrave;nh ảnh cao, độ cong ph&ugrave; hợp để c&oacute; c&aacute;i nh&igrave;n bao qu&aacute;t v&agrave; c&ocirc;ng nghệ HDR gi&uacute;p chất lượng h&igrave;nh ảnh ch&acirc;n thật, sống động như thực tế sẽ gi&uacute;p ch&uacute;ng ta tận hưởng được những n&eacute;t đẹp tinh t&uacute;y của game m&agrave; một chiếc m&agrave;n h&igrave;nh gaming phẳng, FullHD, 24 hay thậm ch&iacute; l&agrave; 27 inch th&ocirc;ng thường kh&oacute; c&oacute; thể l&agrave;m được.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-feature-optimal-curve-true-immersion-295387283.jpg\" style=\"height:1009px; width:1439px\" /></p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y thử tưởng tượng bạn đang chơi Assassin&rsquo;s Creed Valhalla v&agrave; bật chế độ g&oacute;c nh&igrave;n của chim từ tr&ecirc;n cao m&agrave; xem, một chiếc m&agrave;n h&igrave;nh 2K, 32 inch bật HDR đảm bảo sẽ mang lại cho c&aacute;c bạn một c&aacute;i nh&igrave;n kh&aacute;c về vẻ đẹp của v&ugrave;ng đất Viking n&agrave;y đấy.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Với một k&iacute;ch thước m&agrave;n h&igrave;nh lớn 32 inch v&agrave; độ cong 1000R th&igrave; ngo&agrave;i chơi game ra, Samsung LC32G55 c&ograve;n mang đến một trải nghiệm coi phim kh&aacute; l&agrave; ph&ecirc; c&aacute;c bạn ạ. Kể cả nếu so với c&aacute;c m&agrave;n h&igrave;nh phẳng 27 inch th&igrave; coi phim tr&ecirc;n m&agrave;n h&igrave;nh cong 32 inch sẽ cho lại cảm gi&aacute;c đ&atilde; hơn, ch&acirc;n thật hơn, n&oacute;i chung l&agrave; gần giống như l&uacute;c c&aacute;c bạn đi xem phim ngo&agrave;i rạp vậy. Đơn giản v&igrave; m&agrave;n h&igrave;nh c&agrave;ng to th&igrave; d&ugrave;ng c&agrave;ng sướng m&agrave;. Với lại nếu c&aacute;c bạn c&oacute; t&agrave;i khoản Netflix hỗ trợ chất lượng phim l&ecirc;n tới 2K nữa th&igrave; coi như l&agrave; một combo chuẩn b&agrave;i &ldquo;Netflix v&agrave; thư gi&atilde;n&rdquo; rồi c&aacute;c bạn ạ. B&ecirc;n cạnh đ&oacute;, việc sở hữu một k&iacute;ch thước m&agrave;n h&igrave;nh lớn 32 inch sẽ gi&uacute;p c&aacute;c bạn c&oacute; một kh&ocirc;ng gian l&agrave;m việc thoải m&aacute;i hơn c&aacute;c m&agrave;n h&igrave;nh 24 v&agrave; 27 inch rất l&agrave; nhiều. Nhất l&agrave; đối với bạn n&agrave;o hay c&oacute; th&oacute;i quen chia đ&ocirc;i, chia ba m&agrave;n h&igrave;nh để sử dụng song song nhiều cửa sổ ứng dụng c&ugrave;ng một l&uacute;c.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-feature-stunning-wqhd-295387295.jpg\" style=\"height:936px; width:1440px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Để đi k&egrave;m tương xứng với 4 yếu tố tr&ecirc;n, Samsung LC32G55 c&ograve;n được trang bị tần số qu&eacute;t 144Hz, tốc độ phản hồi 1ms v&agrave; hỗ trợ c&ocirc;ng nghệ Freesync Premium mới nhất đến từ AMD gi&uacute;p game thủ c&oacute; được một trải nghiệm mượt m&agrave; trong từng khung h&igrave;nh, tr&aacute;nh c&aacute;c hiện tượng giật lag hay b&oacute;ng mờ xuất hiện trong qu&aacute; tr&igrave;nh tận hưởng vẻ đẹp của game.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-feature-1ms-response-time-295387277.jpg\" style=\"height:1204px; width:1436px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Nếu c&aacute;c bạn l&agrave; người chơi hệ eSport với những tựa game quốc d&acirc;n như Li&ecirc;n Minh Huyền Thoại hay Call of Duty: Warzone th&igrave; Samsung LC32G55 cũng l&agrave; một sản phẩm m&agrave; c&aacute;c bạn n&ecirc;n c&acirc;n nhắc. Bởi v&igrave; một chiếc m&agrave;n h&igrave;nh lớn sẽ gi&uacute;p c&aacute;c bạn c&oacute; một c&aacute;i nh&igrave;n rộng v&agrave; bao qu&aacute;t trận đấu hơn. V&iacute; dụ như bạn c&oacute; thể quan s&aacute;t được cắm mắt đặt ở một vị tr&iacute; xa m&agrave; th&ocirc;ng thường ở m&agrave;n h&igrave;nh 24 inch hay thậm ch&iacute; l&agrave; 27 inch, c&aacute;c bạn sẽ phải di chuột để di chuyển m&agrave;n h&igrave;nh mới thấy được. C&ograve;n nếu c&aacute;c bạn l&agrave; d&acirc;n chơi hệ game đua xe th&igrave; chắc GVN 360 tụi m&igrave;nh cũng kh&ocirc;ng cần phải giải th&iacute;ch g&igrave; nhiều nữa, v&igrave; m&agrave;n h&igrave;nh c&agrave;ng rộng v&agrave; tần số qu&eacute;t c&agrave;ng cao th&igrave; đua c&agrave;ng ph&ecirc; đ&uacute;ng kh&ocirc;ng n&agrave;o!</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-curved-c32g55tqwe-lc32g55tqwexxv-detailblack-295387245.jpg\" style=\"height:488px; width:990px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Samsung LC32G55 sở hữu một thiết kế hầm hố đ&uacute;ng chất game thủ với mặt trước được trang tr&iacute; 2 hốc tam gi&aacute;c nằm ở 2 b&ecirc;n m&agrave;n h&igrave;nh nh&igrave;n giống như một chiếc phi thuyền vậy. Mặt sau th&igrave; LC32G55 c&oacute; thiết kế v&ocirc; số c&aacute;c đường sọc nổi c&oacute; hướng tập trung về một ph&iacute;a tạo cho m&igrave;nh một c&aacute;i cảm gi&aacute;c kh&aacute; l&agrave; &ldquo;tương lai&rdquo;, v&agrave; khi phối hợp với thiết kế 2 hốc tam gi&aacute;c ở mặt trước th&igrave; c&agrave;ng nh&igrave;n c&agrave;ng giống như một chiếc phi thuyền ngo&agrave;i h&agrave;nh tinh c&aacute;c bạn ạ.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/07/vn-curved-c32g55tqwe-lc32g55tqwexxv-backlightoffblack-295387255.jpg\" style=\"height:532px; width:1096px\" /></p>\r\n\r\n<p style=\"text-align:justify\">T&oacute;m lại, Samsung LC32G55 sẽ l&agrave; chiếc m&agrave;n h&igrave;nh ph&ugrave; hợp đối với những bạn n&agrave;o th&iacute;ch sự hầm hố, vừa muốn được tận hưởng sự đẹp đẽ v&agrave; ch&acirc;n thật trong h&igrave;nh ảnh, cũng như l&agrave; vừa muốn trải nghiệm sự mượt m&agrave; trong từng khung h&igrave;nh từ trong game cho tới c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y.</p>\r\n', 'Bình thường', 1),
(29, 8, 'CPU AMD chính thức thoát kiếp bị “bóp” hiệu năng trên Windows 11', 'blog2.jpg', '2021-11-23 14:48:13', '2021-11-13 12:11:30', 'Bản cập nhật Windows 11 khắc phục triệt để vấn đề CPU AMD bị giảm hiệu năng đã được tung ra. Mời các bạn tải về.                                                ', '<p style=\"text-align:justify\">Microsoft vừa mới tung ra bản cập nhật &ldquo;cumulative update&rdquo; (KB5007215) d&agrave;nh cho Windows 11, giải quyết triệt để vấn đề &ldquo;b&oacute;p&rdquo; hiệu năng CPU AMD v&agrave; sửa một số lỗi kh&aacute;c của Windows 11. Trước đ&acirc;y, AMD c&oacute; tung ra bản cập nhật khắc phục lỗi n&agrave;y, nhưng giờ th&igrave; n&oacute; đ&atilde; được t&iacute;ch hợp v&agrave;o trong hệ điều h&agrave;nh lu&ocirc;n. Điều n&agrave;y c&oacute; nghĩa l&agrave; CPU AMD sẽ chạy đ&uacute;ng với hiệu năng của n&oacute;, v&agrave; bạn kh&ocirc;ng cần phải c&agrave;i th&ecirc;m bản cập nhật n&agrave;o nữa từ AMD hay Microsoft.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/CPU-AMD-chinh-thuc-thoat-kiep-bi-bop-hieu-nang-tren-Windows-11-2-1068x580.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Theo th&ocirc;ng tin tr&ecirc;n trang web hỗ trợ của Microsoft th&igrave; họ kh&ocirc;ng cho biết th&ecirc;m chi tiết về vụ CPU AMD, chỉ đơn thuần n&oacute;i l&agrave; đ&atilde; sửa được lỗi bộ nhớ đệm L3 khiến một số m&aacute;y t&iacute;nh trang bị CPU AMD gặp vấn đề về hiệu năng m&agrave; th&ocirc;i. Một lỗi kh&aacute;c cũng được sửa trong bản cập nhật n&agrave;y l&agrave; lỗi Start v&agrave; taskbar hoạt động kh&ocirc;ng ổn định sau khi cập nhật l&ecirc;n từ Windows 10.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/CPU-AMD-chinh-thuc-thoat-kiep-bi-bop-hieu-nang-tren-Windows-11-1-1068x580.jpg\" style=\"height:580px; width:1068px\" /></p>\r\n\r\n<p style=\"text-align:justify\">B&igrave;nh thường th&igrave; Windows 11 sẽ tự động cập nhật bản v&aacute; lỗi n&agrave;y. C&ograve;n nếu bạn chủ động y&ecirc;u cầu m&aacute;y t&iacute;nh tạm dừng cập nhật th&igrave; h&atilde;y bấm n&uacute;t cho n&oacute; kiểm tra bản cập nhật mới theo c&aacute;ch thủ c&ocirc;ng nh&eacute;. Lần n&agrave;y, Microsoft c&oacute; vẻ như rất biết lắng nghe người d&ugrave;ng, nhanh ch&oacute;ng sửa những lỗi xuất hiện trong đợt ra mắt Windows 11 vừa rồi.</p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>T&oacute;m tắt &yacute; ch&iacute;nh:</strong></span></h2>\r\n\r\n<p>- Bản cập nhật &ldquo;cumulative update&rdquo; (KB5007215) d&agrave;nh cho Windows 11 đ&atilde; sửa lỗi CPU AMD bị &ldquo;b&oacute;p&rdquo; hiệu năng</p>\r\n\r\n<p>-&nbsp;Microsoft cho biết họ đ&atilde; sửa được lỗi bộ nhớ đệm L3 khiến PC trang bị CPU AMD bị giảm hiệu năng</p>\r\n\r\n<p>- Bản cập nhật n&agrave;y cũng sửa th&ecirc;m lỗi Start v&agrave; taskbar hoạt động kh&ocirc;ng ổn định</p>\r\n', 'Nổi bật', 1),
(30, 5, 'Microsoft ra mắt Windows 11 SE tối ưu cho học tập', 'blog3.jpg', '2021-11-23 14:47:34', '2021-11-13 12:16:58', 'Hệ điều hành Windows 11 SE được thiết kế để học sinh có thể tập trung vào vào việc học tốt hơn.                                                ', '<p style=\"text-align:justify\">Microsoft vừa mới giới thiệu 2 sản phẩm mới d&agrave;nh cho việc học tập, bao gồm Surface Laptop SE v&agrave; phi&ecirc;n bản Windows 11 mới gọi l&agrave; Windows 11 SE. Những sản phẩm n&agrave;y hướng đến ph&acirc;n kh&uacute;c gi&aacute;o dục v&agrave; được thiết kế để c&oacute; thể lắp đặt v&agrave; triển khai nhanh ch&oacute;ng ở c&aacute;c bậc tiểu học v&agrave; trung học. Mục ti&ecirc;u của Microsoft c&oacute; lẽ l&agrave; để chiếm lại thị phần vốn đang nằm trong tay Google Chromebooks.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/Microsoft-ra-mat-Windows-11-SE-toi-uu-cho-hoc-tap-1-1068x580.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Trong bối cảnh c&agrave;ng ng&agrave;y c&agrave;ng c&oacute; nhiều học sinh sử dụng m&aacute;y t&iacute;nh để học tập, Microsoft muốn gi&uacute;p học sinh c&oacute; được trải nghiệm học tập tốt nhất c&oacute; thể bằng c&aacute;ch tung ra những mẫu laptop với mức gi&aacute; dễ tiếp cận, c&ugrave;ng với đ&oacute; l&agrave; hệ điều h&agrave;nh mới gi&uacute;p học sinh dễ d&agrave;ng tập trung v&agrave;o việc học hơn.</p>\r\n\r\n<p style=\"text-align:justify\">Windows 11 SE l&agrave; hệ điều h&agrave;nh tương tự Chromebooks. N&oacute; c&oacute; giao diện người d&ugrave;ng (UI) được tinh giản v&agrave; t&iacute;ch hợp c&aacute;c c&ocirc;ng cụ quản l&yacute; d&agrave;nh cho m&ocirc;i trường học đường. Cơ bản th&igrave; đ&acirc;y l&agrave; Windows 11 Home phi&ecirc;n bản bị giới hạn một số chức năng v&agrave; t&ugrave;y chỉnh (chỉ c&oacute; bộ phận IT mới c&oacute; quyền truy cập) để học sinh tập trung v&agrave;o việc học.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/Microsoft-ra-mat-Windows-11-SE-toi-uu-cho-hoc-tap-2.jpg\" style=\"height:675px; width:1200px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Thay đổi lớn nhất của hệ điều h&agrave;nh n&agrave;y l&agrave; n&oacute; kh&ocirc;ng c&oacute; Windows Store v&agrave; người d&ugrave;ng kh&ocirc;ng thể tự c&agrave;i ứng dụng x86/Win32. Nếu muốn c&agrave;i th&igrave; phải d&ugrave;ng c&ocirc;ng cụ chuy&ecirc;n dụng của bộ phận IT. Nhưng cho d&ugrave; bạn c&oacute; quyền IT th&igrave; cũng kh&ocirc;ng hẳn muốn c&agrave;i phần mềm n&agrave;o l&agrave; c&agrave;i đ&acirc;u. Chỉ c&oacute; một số ứng dụng với c&aacute;c chức năng như lọc nội dung, l&agrave;m b&agrave;i kiểm tra, hỗ trợ dạy v&agrave; học, tr&igrave;nh duyệt web th&igrave; mới được c&agrave;i v&agrave;o m&aacute;y. Những ứng dụng kh&ocirc;ng nằm trong c&aacute;c mục n&agrave;y (chẳng hạn như Steam) l&agrave; thua rồi đ&oacute;. C&oacute; một ngoại lệ l&agrave; dường như những ứng dụng chạy tr&ecirc;n nền web l&agrave; vẫn ngon l&agrave;nh, kh&ocirc;ng bị giới hạn g&igrave; cả.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/NsQyGwn4tNA2CYEdEDbd8K-2048x1152.jpg\" style=\"height:675px; width:1200px\" /></p>\r\n\r\n<p style=\"text-align:justify\">N&oacute;i sơ về Surface Laptop SE c&oacute; gi&aacute; từ 249,99 USD, v&agrave; đ&acirc;y cũng l&agrave; thiết bị c&oacute; mức gi&aacute; mềm nhất của Microsoft (t&iacute;nh đến thời điểm b&agrave;i viết). Chiếc laptop n&agrave;y c&oacute; cấu h&igrave;nh gồm CPU Intel Celeron N4020 (2 nh&acirc;n) hoặc N4120 (4 nh&acirc;n), 4GB hoặc 8GB RAM DDR4, 64GB hoặc 128GB bộ nhớ eMMC flash. M&agrave;n h&igrave;nh l&agrave; loại TFT 11,6-inch (1366&times;768). C&aacute;c cổng kết nối bao gồm 1 cổng USB Type-C, 1 cổng USB Type-A, 1 cổng nguồn DC, 1 cổng combo 3,5mm headphone/mic. Laptop hỗ trợ kết nối WiFi 5 (802.11ac) v&agrave; Bluetooth 5.0 LE. M&aacute;y c&oacute; m&agrave;u trắng &ldquo;Glacier&rdquo;, trọng lượng 1,11 kg, v&agrave; thời lượng pin l&ecirc;n đến 16 tiếng.</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>T&oacute;m tắt &yacute; ch&iacute;nh:</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\">- Microsoft muốn gi&uacute;p học sinh c&oacute; được trải nghiệm học tập tốt nhất với hệ điều h&agrave;nh Windows 11 SE</p>\r\n\r\n<p style=\"text-align:justify\">- N&oacute; c&oacute; giao diện đơn giản v&agrave; t&iacute;ch hợp c&aacute;c c&ocirc;ng cụ quản l&yacute; d&agrave;nh cho m&ocirc;i trường học đường</p>\r\n\r\n<p style=\"text-align:justify\">- Hệ điều h&agrave;nh kh&ocirc;ng c&oacute; Windows Store v&agrave; chỉ c&oacute; bộ phận IT mới c&oacute; thể c&agrave;i ứng dụng x86/Win32 l&ecirc;n m&aacute;y</p>\r\n\r\n<p style=\"text-align:justify\">- C&aacute;c ứng dụng c&agrave;i v&agrave;o m&aacute;y cũng phải thuộc loại hỗ trợ cho việc học tập, chứ kh&ocirc;ng phải muốn c&agrave;i g&igrave; th&igrave; c&agrave;i</p>\r\n', 'Bình thường', 1),
(31, 7, 'Lộ tin CPU Intel Core i5-12400F mạnh ngang AMD Ryzen 5 5600X với giá chỉ bằng một nửa', 'blog4.jpg', '2021-11-23 14:48:11', '2021-11-13 12:23:30', 'Rò rỉ thông tin CPU Intel Core i5-12400F có mức Base Frequency Power 65 W, đầu năm 2022 ra mắt.                        ', '<p style=\"text-align:justify\">Comptoir Hardware &ndash; một trang web c&ocirc;ng nghệ của Ph&aacute;p &ndash; vừa mới r&ograve; rỉ th&ocirc;ng tin về con chip Intel Core i5-12400F chứa 6 nh&acirc;n 6 luồng. Đ&acirc;y được xem như l&agrave; CPU tầm trung với mức Base Frequency Power (TDP) l&agrave; 65 W, dự kiến phải đến đầu năm 2022 mới ra mắt.</p>\r\n\r\n<p style=\"text-align:justify\">Tiếp đ&oacute;, trang VideoCardz c&oacute; bổ sung th&ocirc;ng tin từ MSI. Họ cho biết Intel Core i5-12400F sử dụng die mới, chỉ c&oacute; 6 nh&acirc;n hiệu năng cao &ldquo;Golden Cove&rdquo; m&agrave; th&ocirc;i, kh&ocirc;ng hề c&oacute; nh&acirc;n tiết kiệm điện &ldquo;Gracemont&rdquo; xuất hiện ở đ&acirc;y.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400f_cpuz-1024x340.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y c&oacute; vẻ như l&agrave; con CPU non-K đầu ti&ecirc;n được đem ra test nhiều đến như vậy trước ng&agrave;y ra mắt ch&iacute;nh thức, v&agrave; đ&acirc;y cũng l&agrave; phi&ecirc;n bản thử nghiệm (ES &ndash; Engineering Sample). Bo mạch chủ được đem ra thử nghiệm chung với con chip n&agrave;y được c&agrave;i đặt mức năng lượng trần (power limit) l&agrave; 117 W, v&agrave; b&agrave;i test được chạy trong m&ocirc;i trường Windows 11, c&ugrave;ng với RAM DDR5 v&agrave; GPU AMD Radeon RX 6900 XT.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-Power-768x622.jpg\" style=\"height:622px; width:768px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Con số 117 W được cho l&agrave; mức Maximum Turbo Power (PL2), hoặc c&oacute; thể l&agrave; một t&iacute;nh năng ri&ecirc;ng tr&ecirc;n con chip được đem ra thử nghiệm. Mức xung nhịp của n&oacute; dao động trong khoảng từ 800 MHz l&ecirc;n đến 4,4 GHz (đơn nh&acirc;n). Sau khi đạt tới ngưỡng Tau (khoảng thời gian m&agrave; CPU c&oacute; thể chạy ở trạng th&aacute;i PL2) th&igrave; xung nhịp rớt xuống c&ograve;n 3,4 GHz.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-CB23-ST-768x647.jpg\" style=\"height:647px; width:768px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-CB23-MT-768x643.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-CPUZ-ST-768x610.jpg\" style=\"height:610px; width:768px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-CPUZ-MT-768x651.jpg\" style=\"height:651px; width:768px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Intel Core i5-12400F c&oacute; vẻ như sẽ l&agrave; đối thủ nặng k&yacute; của AMD Ryzen 5 5600X (6 nh&acirc;n 12 luồng). Điều kh&aacute;c biệt lớn nhất giữa 2 con CPU n&agrave;y l&agrave; con chip của Intel c&oacute; thể sẽ sở hữu mức gi&aacute; thấp hơn so với b&ecirc;n AMD (Intel Core i5-11400F c&oacute; gi&aacute; khoảng 157 USD, gần bằng 1 nửa so với AMD Ryzen 5 5600X). Cả 2 CPU đều c&oacute; hiệu năng tốt khi chiến game. Vấn đề lớn nhất ở đ&acirc;y l&agrave; mức điện năng ti&ecirc;u thụ của 2 con chip n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"https://gstatic.gvn360.com/2021/11/12400F-Gaming-768x652.jpg\" style=\"height:652px; width:768px\" /></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>T&oacute;m tắt &yacute; ch&iacute;nh:</strong></span></h2>\r\n\r\n<p>- Intel Core i5-12400F chứa 6 nh&acirc;n 6 luồng, Base Frequency Power (TDP) 65 W, dự kiến ra mắt đầu năm 2022</p>\r\n\r\n<p>- Intel i5-12400F sử dụng die mới, chỉ c&oacute; 6 nh&acirc;n hiệu năng cao &ldquo;Golden Cove&rdquo; m&agrave; th&ocirc;i</p>\r\n\r\n<p>- Mức xung nhịp của n&oacute; dao động trong khoảng từ 800 MHz l&ecirc;n đến 4,4 GHz (đơn nh&acirc;n)</p>\r\n\r\n<p>- Intel Core i5-12400F c&oacute; thể sở hữu mức gi&aacute; thấp hơn so với AMD Ryzen 5 5600X (Intel Core i5-11400F c&oacute; gi&aacute; khoảng 157 USD, gần bằng 1 nửa so với AMD Ryzen 5 5600X)</p>\r\n', 'Bình thường', 1);

